// https://jsonplaceholder.typicode.com/todos
import{useEffect, useState} from "react";
import "./index.css"
import{Link, useNavigate} from "react-router-dom"


function EmpList(){
   const  [data,setData]=useState(null)
   const navigate=useNavigate()
   const [value,setValue]=useState("")
   useEffect(()=>{
       fetch("http://localhost:3006/Employee")
       .then((res)=>{
           return res.json()
       })
       .then((resp)=>{
           console.log(resp)
           setData(resp)
       })
        
   },[])
   const deleteData=(id)=>{
    if(window.confirm("Do You Want To Delete it...?")){
   fetch("http://localhost:3006/Employee/"+id,{
    method:"DELETE"
   })
   .then((s)=>{
    alert("Deleted successfully")
    window.location.reload()
   })
   .catch((err)=>{
    console.log(err)
   })
}
   
   }
   const EditData=(id)=>{
    navigate("/empedit/"+id)
   }
   const DetailsData=(id)=>{
    navigate("/empdetails/"+id)
   }
   const updateUser=(e)=>{
     setValue(e.target.value)
   }
   
   return(
       <div className="container">
        <div className="card">
            <div className="card-title">
              <h1>EMPLOYEE MANAGEMENT SYSTEM</h1>
            </div>
            <div className="card-body">
          <Link to="./form"  className="btn btn-success">Add New(+)</Link>
          <form>
          <input value={value} onChange={updataUser} type="text" placeholder="Filter Record's"  className="form-control" id="exampleInputPassword1"/>
          <button type="submit">Search</button>
          </form>
         <table className="table table-bordred">
           <thead>
               <tr className="table-dark text-white">
                   <th>userId</th>
                   <th>name</th>
                   <th>city</th>
                   <th>Mobile No</th>
                   <th>Action</th>
               </tr>

           </thead>
           <tbody>
               {data?.map((item)=>(


               <tr>
                   
                    <td>
                        
                            < input type="checkbox" width="10px"/>
                            
                        
                        {item.id}</td>
                   <td>{item.name}</td>
                   <td>{item.city}</td>
                   <td>{item.mobile}</td>
                   <td>
                    <button onClick={()=>{deleteData(item.id)}} className="btn btn-danger">Delete</button>
                    <button onClick={()=>{EditData(item.id)}} className="btn btn-primary">Edit</button>
                    <button onClick={()=>{DetailsData(item.id)}} className="btn btn-success">Show Data</button>
                   </td>
               </tr>
                               ))}

           </tbody>
         </table>
       </div>
       </div>
       </div>
   )
}
export default EmpList;
