import React from "react"
class Form extends React.Component{
    constructor(){
        super();
        this.state={
            username:"",
            email:"",
            pswd:"",
            cpswd:""
        }
    }
    updateUser=(e)=>{
        console.log(e.target.value)
        this.setState({
            user:e.target.value
        })
    }
    updateEmail=(e)=>{
        this.setState({
            email:e.target.value
        })
    }
    updatePassword=(e)=>{
        this.setState({
            pswd:e.target.value
        })
    }
    updateCpassword=(e)=>{
        this.setState({
            cpswd:e.target.value
        })
    }
    submitData=(e)=>{
        e.preventDefault()
        if(this.state.user.length<=5){
            alert("Username should be >5 characters")
        }
        else if(this.state.pswd !== this.state.cpswd){
            alert("Password does not match")
        }
        else{
         alert("Form is successfully Submitted")
         this.storeData()
        
        }
        
    }
    storeData=()=>{
        localStorage.setItem("username",this.state.username)
        localStorage.setItem("email",this.state.email)
        localStorage.setItem("password",this.state.pswd)
    
    }
    render(){
        return(
            <div>
                <form onSubmit={this.submitData}>
                    <label>Username:</label>
                    <input value={this.state.username}type="text"required onChange={this.updateUser}/>
                    <br/><br/>
                    <label>Email</label>
                    <input value={this.state.email} type="email" reqired onChange={this.updateEmail}/>
                    <br/><br/>
                    <label>Password</label>
                    <input value={this.state.pswd} type="password" required onChange={this.updatePassword}/>
                    <br/><br/>
                    <label>confirmPassword</label>
                    <input value={this.state.cpswd} type="password" required onChange={this.updatedCpassword}/>
                    <br/><br/>
                    <input type="submit" value="Registered"/>
                </form>
            </div>
        )
    }
}
export default Form;
