// https://jsonplaceholder.typicode.com/todos
import{useEffect, useState} from "react";
import "./index.css"
import{Link} from "react-router-dom"


function EmpList(){
   const  [data,setData]=useState(null)
   useEffect(()=>{
       fetch("http://localhost:3006/Employee")
       .then((res)=>{
           return res.json()
       })
       .then((resp)=>{
           console.log(resp)
           setData(resp)
       })
        
   },[])
   const deleteData=(id)=>{
    if(window.confirm("Do You Want To Delete it...?")){
   fetch("http://localhost:3006/Employee/"+id,{
    method:"DELETE"
   })
   .then((s)=>{
    alert("Deleted successfully")
    window.location.reload()
   })
   .catch((err)=>{
    console.log(err)
   })
}
   
   }
   
   return(
       <div className="container">
        <div className="card">
            <div className="card-title">
              <h1>EMPLOYEE MANAGEMENT SYSTEM</h1>
            </div>
            <div className="card-body">
          <Link to="./form"  className="btn btn-success">Add New(+)</Link>
         <table className="table table-bordred">
           <thead>
               <tr className="table-dark text-white">
                   <th>userId</th>
                   <th>name</th>
                   <th>city</th>
                   <th>Mobile No</th>
                   <th>Action</th>
               </tr>

           </thead>
           <tbody>
               {data?.map((item)=>(


               <tr>
                   <td>{item.id}</td>
                   <td>{item.name}</td>
                   <td>{item.city}</td>
                   <td>{item.mobile}</td>
                   <td>
                    <a onClick={()=>{deleteData(item.id)}} className="btn btn-danger">Delete</a>
                   </td>
               </tr>
                               ))}

           </tbody>
         </table>
       </div>
       </div>
       </div>
   )
}
export default EmpList;
