 import React from "react"
      class Lifecycle extends React.Component{
        constructor()
        {
            super();
            this.state={
                name:"Welcome All"
            }
        }
        render(){
            return(
                <div>
                    <h1>life cycles</h1>
                    {this.state.name}
                </div>
            )
        }
      }
       export default Lifecycle;
