// Inheritance
import React from "react";

class App extends React.Component{
    render(){
        return(
            <div>
                <h1>Class Component</h1>
            </div>
        )
    }
}
class App1 extends App{
    render(){
        const parent=super.render()
        return(
            <div>
                {parent}
                <h1>Parent Component</h1>
            </div>
        )
    }
}
class App2 extends App1{
    render(){
        const parent=super.render()
        return(
            <div>
                <h1>Child Component</h1>
            </div>
        )
    }
}
export default App2
