import React from "react"
class Formvalid extends React.Component{
    constructor(){
        super();
            this.state={
                user:"",
                email:"",
                pswd:"",
                cpswd:""

            
        }
    }
    updateUser=(e)=>{
        console.log(e.target.value)
        this.setState({
            user:e.target.value
        })
    }
    render(){
        return(
            <div>
                <form>
                    <label>Username:</label>
                    <input value={this.state.user} type="text" onChange={this.updateUser}/>
                    <br/><br/>
                    <label>Email:</label>
                    <input value={this.state.email} type="email"/>
                    <br/><br/>
                    <label>Password</label>
                    <input value={this.state.pswd} type="password"/>
                    <br/><br/>
                    <label>confirmPassword</label>
                    <input value={this.state.cpswd} type="password"/>
                    <br/><br/>
                    <input type="submit" value="Registered"/>
                </form>
            </div>
        )
    }
}
export default Formvalid;
