import React from "react"
class Formvalid extends React.Component{
    constructor(){
        super();
            this.state={
                user:"",
                email:"",
                pswd:"",
                cpswd:""

            
        }
    }
    updateUser=(e)=>{
        console.log(e.target.value)
        this.setState({
            user:e.target.value
        })
    }
    updateEmail=(e)=>{
        this.setState({
            email:e.target.value
        })
    }
    updatePassword=(e)=>{
        this.setState({
            pswd:e.target.value
        })
    }
    updateCpassword=(e)=>{
        this.setState({
            cpswd:e.target.value
        })
    }
    submitData=(e)=>{
        if(this.state.user.length<=5){
            alert("user should be >5 characters")
        }
        else if(this.state.pswd!== this.state.cpswd){
            alert("password does not match")
        }
        else{
            alert("form is submitted successfully")
            this.storeData()
        }
    }
    storeData=(e)=>{
        localStorage.setItem("user",this.state.user)
        localStorage.setItem(" email",this.state.email)
        localStorage.setItem("pswd",this.state("password",this.state.pswd))
    }
    
    render(){
        return(
            <div>
                <form onSubmit={this.submitData}>
                    <label>Username:</label>
                    <input value={this.state.user} type="text" required onChange={this.updateUser}/>
                    <br/><br/>
                    <label>Email:</label>
                    <input value={this.state.email} type="email" required onChange={this.updateEmail}/>
                    <br/><br/>
                    <label>Password</label>
                    <input value={this.state.pswd} type="password"  required onChange={this.updatePassword}/>
                    <br/><br/>
                    <label>confirmPassword</label>
                    <input value={this.state.cpswd} type="password" required  onChange={this.updateCpassword}/>
                    <br/><br/>
                    <input type="submit" value="Registered"/>
                </form>
            </div>
        )
    }
}
export default Formvalid;
