 import{useState} from "react"
function Hooks(){
     const [data,setData]=useState("React js")
     const [count,setCount]=useState(0)
     const change=()=>{
        setData("hello")
     }
     const inc=()=>{
        setCount(count+1)
     }
    return(
        <div>
             <h1>Hooks Method</h1>
             {data}
             <Hooks value={data}count={count}/>
             <button onClick={change}>Update</button>
             <br/><br/>
             <p>Count:{count}</p>
             <button onClick={inc}>inc</button>
        </div>
    )
}
function Hooks1(props){
