import React from "react" ;
class Login extends React.Component{
    constructor(){
        super();
            this.state={
                user:"",
                email:"",
                pswd:"",
                cpswd:""

            
        }
    }
    updateUser=(e)=>{
        console.log(e.target.value)
        this.setState({
            user:e.target.value
        })
    }
    updateEmail=(e)=>{
        this.setState({
            email:e.target.value
        })
    }
    updatePassword=(e)=>{
        this.setState({
            pswd:e.target.value
        })
    }
    updateCpassword=(e)=>{
        this.setState({
            cpswd:e.target.value
        })
    }
    submitForm=(e)=>{
     let x=   localStorage.getItem("user")
      let y=  localStorage.getItem("email")
     let z=   localStorage.getItem("password")
        if(this.state.user!==x || this.state.email!== y || this.state.pswd!==z){
            alert("check the credientials")
        }
        else{
            alert("logged in")
            
        }

        }
    
    render(){
        return(
            <div>
                <h2>Login</h2>
                <form onSubmit={this.submitForm}>
                <label>Username:</label>
                    <input value={this.state.user} type="text" required onChange={this.updateUser}/>
                    <br/><br/>
                    <label>Email:</label>
                    <input value={this.state.email} type="email" required onChange={this.updateEmail}/>
                    <br/><br/>
                    <label>Password</label>
                    <input value={this.state.pswd} type="password"  required onChange={this.updatePassword}/>
                    <br/><br/>
                    <label>confirmPassword</label>
                    <input value={this.state.cpswd} type="password" required  onChange={this.updateCpassword}/>
                    <br/><br/>
                    <input type="submit" value="Login"/>
                </form>
            </div>
        )
    }
}
export default Login;
